questions.question:
Welches Objekt wird bei folgendem Code aufgerufen?

interface Notifier { send(msg: string): void }
class Email implements Notifier { send(msg: string) { console.log('Email', msg) } }
class SMS implements Notifier { send(msg: string) { console.log('SMS', msg) } }
const n: Notifier = new SMS();
n.send('Hallo')
